import React, {Component} from 'react';
import './TodoList.css';

class TodoList extends Component {

    constructor(props) {
        super(props);
        this.state = {
            userInput: '',
            items: []
        };
    }

    componentDidMount() {
        fetch('/api/TodoList')
            .then(response => response.json())
            .then(list => this.setState({items: list}))
    }

    onChange(event) {
        this.setState({
            userInput: event.target.value
        });
    }

    addTodo(event) {
        event.preventDefault();

        fetch('/api/TodoList', {
            method: 'POST',
            body: JSON.stringify([...this.state.items, this.state.userInput])
        })
            .then(res => res.json())
            .then(response =>
                this.setState({
                    items: response,
                    userInput: ''
                }));
    }


    removeTodo(event) {
        event.preventDefault();
        const array = this.state.items;
        array.splice(array.indexOf(event.target.value), 1);
        this.setState({
            items: array
        });

        fetch('/api/TodoList', {
            method: 'DELETE',
            body: JSON.stringify(this.state.items)
        }).then(res => res.json())
            .then(response => console.log(response))
    }

    renderTodos() {
        return this.state.items.map((item) => {
            return (
                <div className="item" key={item}>
                    {item}
                    <button type='submit' value={item} onClick={this.removeTodo.bind(this)}>X</button>
                </div>
            );
        })
    }


    render() {
        return (
            <div className="container">
                <h1>Todo List </h1>
                <form method="POST">
                    <input
                        value={this.state.userInput}
                        type="text"
                        placeholder="Ajouter une todo"
                        onChange={this.onChange.bind(this)}
                    />
                    <button type='submit' onClick={this.addTodo.bind(this)}> Ajouter</button>
                </form>
                <div className="list">
                    {this.renderTodos()}
                </div>
            </div>
        )
    }
}

export default TodoList;
