import React, { Component } from 'react';
import './App.css';

import TodoList from './Todo/TodoList'

class App extends Component {
    constructor(props){
        super(props);
        this.state = {
            list: []
        }
    }

    componentDidMount() {
        this.getList();
    }


    getList = () => {
        fetch('/api/getList')
            .then(res => res.json())
            .then(list => this.setState({ list }))
    }

  render() {
    return (

        <TodoList/>

    );
  }
}

export default App;
